// !    rfc
import React, { useState } from "react";
import './Editor.css';

let color = {
  // color: "white",
};

let bgco = {
  // border: " 1px dashed",
};


// ^ for capitalize
const capitalize = (str) => {
  return str
    .toLowerCase() // ?Convert the entire string to lowercase
    .split(" ") // ?Split the string into an array of words using space as a delimiter
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1)) // ?For each word in the array, capitalize the first letter and concatenate it with the rest of the word
    .join(" "); // ?Join the words back into a string with spaces in between
};

export default function TextVibeForm(props) {
  const [text, setText] = useState("");
  const [fontFamily, setFontFamily] = useState("Arial, sans-serif");

  const setFontFamilyHandler = (family) => {
    setFontFamily(family);
    props.showAlert(` converted to ${family}`, "success");

  };

  // ^ for uppercase
  const handelonclick = () => {
    // console.log("On Click");
    let Newtxt = text.toUpperCase();
    setText(Newtxt);
    props.showAlert("Converted to UpperCase", "success");
  };

  // ^ for lowercase
  const handellowclick = () => {
    // console.log("On Click");
    let Newtxt = text.toLowerCase();
    setText(Newtxt);
    props.showAlert("Converted to LowerCase", "success");
  };

  // ^ for capitalize
  const handelcapclick = () => {
    // console.log("On Click");
    let Newtxt = capitalize(text);
    setText(Newtxt);
    props.showAlert("Capitalized", "success");
  };

  // ^ for clear
  const handelclearclick = () => {
    // console.log("On Click");
    let Newtxt = "";
    setText(Newtxt);
    props.showAlert("Text Cleared", "success");
  };

  // ^ for copy
  const handelcopyclick = () => {
    // console.log("On Click");
    let Newtxt = document.getElementById("box");
    Newtxt.select();
    navigator.clipboard.writeText(Newtxt.value);
    props.showAlert("Text Copied", "success");
  };

  // ^ for remove extra space
  const handelremoveextraspacesclick = () => {
    // console.log("On Click");
    let Newtxt = text.split(/[ ]+/);
    setText(Newtxt.join(" "));
    props.showAlert("Extra Space Removed", "success");
  };

  const handleDownloadClick = () => {
    const element = document.createElement("a");
    const file = new Blob([text], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = "textFile.txt";
    document.body.appendChild(element); // Required for this to work in Firefox
    element.click();
    document.body.removeChild(element); // Clean up
  };

  // ^ for change the value of input field and text area
  const handelonchange = (event) => {
    // console.log("On change");
    setText(event.target.value);
    // props.showAlert("Capitalized","success");
  };

  // ^ for select question marks from indices 1 to 100000
  const handelQuestionClick = () => {
    const textarea = document.getElementById("box");
    const textValue = textarea.value;

    // Get all indices of question marks in the text
    const questionMarksIndices = getAllQuestionMarksIndices(textValue);

    if (questionMarksIndices.length > 0) {
      // alert(`Found ${questionMarksIndices.length} question mark(s)!`);
      props.showAlert(
        `Found ${questionMarksIndices.length} question mark(s)!`,
        "success"
      );

      // Clear existing selection
      textarea.setSelectionRange(0, 0);

      // Select question marks in the range from index 1 to 100000
      const selectedIndices = questionMarksIndices.filter(
        (index) => index >= 1 && index <= 100000
      );

      selectedIndices.forEach((index) => {
        textarea.setSelectionRange(index, index + 1);
      });

      textarea.focus();
    } else {
      props.showAlert("No Question Mark Found", "danger");
    }
  };

  const handleSpeakClick = (event) => {
    let el = event.currentTarget;
    if (el.innerHTML === "Listen Now") el.innerHTML = "Stop Now";
    else el.innerHTML = "Listen Now";

    // el.innerHTML has already been changed here, hence checking for the opposite value
    if (el.innerHTML === "Stop Now") {
      let msg = new SpeechSynthesisUtterance();
      msg.text = text; // Use 'text' instead of 'textState.text'
      window.speechSynthesis.speak(msg);
    } else {
      let msg = new SpeechSynthesisUtterance();
      msg.text = text; // Use 'text' instead of 'textState.text'
      window.speechSynthesis.cancel(msg);
    }
  };

  // // ^ handel speak click
  //  const handleSpeakClick = (event) => {
  //   let el = event.currentTarget;
  //   if (el.innerHTML === 'Listen Now') el.innerHTML = 'Stop Now';
  //   else el.innerHTML = 'Listen Now';

  //   // el.innerHTML has already been changed here, hence checking for the opposite value
  //   if (el.innerHTML === 'Stop Now') {
  //     let msg = new SpeechSynthesisUtterance();
  //     msg.text = textState.text;
  //     window.speechSynthesis.speak(msg);
  //   } else {
  //     let msg = new SpeechSynthesisUtterance();
  //     msg.text = textState.text;
  //     window.speechSynthesis.cancel(msg);
  //   }
  // };

  // Function to get all indices of question marks in the text
  const getAllQuestionMarksIndices = (textValue) => {
    const indices = [];
    let index = textValue.indexOf("?");

    while (index !== -1) {
      indices.push(index);
      index = textValue.indexOf("?", index + 1);
    }

    return indices;
  };
  // ^ for extract numbers
const handelextractnumberclick = () => {
  // console.log("On Click");
  const numbers = text.match(/\d+/g);
  if (numbers) {
    const extractedNumbers = numbers.join(", ");
    setText(extractedNumbers);
    props.showAlert("Numbers Extracted: " + extractedNumbers, "success");
  } else {
    props.showAlert("No Numbers Found", "danger");
  }
};
// ^ for remove numbers
const handelremovenumbersclick = () => {
  // console.log("On Click");
  let Newtxt = text.replace(/\d+/g, '');
  setText(Newtxt);
  props.showAlert("Numbers Removed", "success");
};
  return (
    <>
      <div
        className={`container rounded bg-co `}
        style={bgco}
       >
        <div className="my-3">
          <h5>{props.heading}</h5>
          <h6>Text Editor : Word count {text.trim().split(/\s+/).length-1},Character {text.length},Minutes read {0.08 * text.split(" ").length}</h6>
          {/* <h6>Text Editor : Character {text.length}</h6> */}
          <textarea
            className={`form-control my-3  `}
            id="box"
            rows="10"
            value={text}
            onChange={handelonchange}
            placeholder="TextVibe : Enter your text here"
            style={{ fontFamily: fontFamily }}
          ></textarea>
        </div>

        <div className="container   border-dark rounded" style={bgco}>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelonclick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b>Convert to Uppercase</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handellowclick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b>Convert to Lowercase</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelcapclick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
           <b> Capitalize</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelremoveextraspacesclick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
           <b> Remove Extra spaces</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelcopyclick}
           style={{ backgroundColor: '#211c1c',color:'white' }}
          >
           <b> Copy text</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelclearclick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b> Clear text </b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handelQuestionClick}
           style={{backgroundColor: '#211c1c',color:'white' }}
          >
             <b>find question </b>
          </button>

          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handleSpeakClick}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b> On Speak </b>
          </button>

          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={handleDownloadClick}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
             <i class="fa-solid fa-download"> </i>
          </button>
          <button
  className="btn  mx-1 my-1 border-1-secondary button"
  onClick={handelextractnumberclick}
  style={{ backgroundColor: '#211c1c', color: 'white' }}
>
  <b>Extract Numbers</b>
</button>
<button
  className="btn  mx-1 my-1 border-1-secondary button"
  onClick={handelremovenumbersclick}
  style={{ backgroundColor: '#211c1c', color: 'white' }}
>
  <b>Remove Numbers</b>
</button>
        </div>
        &nbsp;
        <h4>Set Fontfamily</h4>
        <div className="container  my-3  border-dark rounded" style={bgco}>
          
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={() => setFontFamilyHandler("Arial, sans-serif")}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
           <b> Arial</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={() => setFontFamilyHandler("Courier New, monospace")}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
           <b> Courier New</b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={() => setFontFamilyHandler("Times New Roman, serif")}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b>Times New Roman </b>
          </button>
          <button
            className="btn  mx-1 my-1 border-1-secondary button"
            onClick={() => setFontFamilyHandler("Algerian")}
            style={{backgroundColor: '#211c1c',color:'white' }}
          >
            <b> Algerian </b>
          </button>
        </div>

        <div className="container my-3   border-dark" style={color}>
          <h5 className="border-top  border-dark ">
          Your text Summary
          </h5>
          
          
          <h6>Preview</h6>
          <p className="border-top border-dark">
            {text}
          </p>
        </div>
      </div>
    </>
  );
}
