export default function Login(props) {
    let border = {
      height: "600px",
      width: "90%",
      maxWidth: "600px",
      borderRadius: "5px",
      margin: "0 auto",
      // fontFamily:"monospace"
    };
  
    let inform = {
      width: "100%",
      maxWidth: "20rem",
      display: "block",
      marginLeft: "auto",
      marginRight: "auto",
      marginTop: "20px",
    };
  
    let con = {
      marginTop: "50px",
      marginLeft: "auto",
      marginRight: "auto",
      // border: "2px solid white",
      // borderRadius: "10px",
      // boxShadow: "4px 4px 4px 4px darkgray",
      borderRadius: "50px",
  background: "#dfdfdf",
  boxShadow: " 20px 20px 100px #5a5a5a,-20px -20px 100px #ffffff",
      width: "90%",
      maxWidth: "400px",
      height: "500px",
      padding: "20px",
      boxSizing: "border-box",
    };
  
    let aligntext = {
      textAlign: "center",
      fontFamily:"monospace"
    };
  
    // Media query for smaller screens
    const smallerScreen = "@media (max-width: 768px)"; // Adjust as needed
  
    return (
      <>
        <form
          className={`container rounded text-${
            props.mode === "light" ? "dark" : "light"
          }`}
          style={border}
        >
          <div className="s" style={con}>
            <h1 style={aligntext}>Text Editor</h1>
            <h3 style={aligntext}>Login here</h3>
            <div className="form-group" style={inform}>
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input
                type="email"
                className="form-control inform"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
                name="email"
                placeholder="Enter email"
              />
            </div>
  
            <div className="form-group" style={inform}>
              <label htmlFor="exampleInputPassword1">Password</label>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
                placeholder="Password"
              />
            </div>
  
            <div className="form-group form-check" style={inform}>
              <input
                type="checkbox"
                className="form-check-input"
                id="exampleCheck1"
              />
              <label className="form-check-label" htmlFor="exampleCheck1">
                Check me out
              </label>
            </div>
  
            <button type="submit" className="btn btn-primary" style={inform}>
              Submit
            </button>
           
            <a href="/"style={inform}>Register here</a>
          </div>
        </form>
        <style>
          {`${smallerScreen} {
            /* Adjust styles for smaller screens */
            form {
              width: 100%;
              padding: 10px;
            }
            .s {
              width:100%;
              padding: 10px;
              margin-top: 20px;
            }
          }`}
        </style>
      </>
    );
  }
