import React from "react";
import './Login.css'
import user_icon from'../Assists/person.png'
import email_icon from'../Assists/email.png'
import password_icon from'../Assists/password.png'

const Login=()=>{
    const [action,setAction] =useState("Login");
    return(
        <div className="container">
            <div className="header">
                <div className="text">{action}</div>
                <div className="underline"></div>
            </div>
            <div className="inputs">
                {action==="Login"?<div></div>: <div className="input">
                    <img src={user_icon} alt="" />
                    <input type="text" placeholder="Name" name="" id="" />
                </div>}
               
                <div className="input">
                    <img src={email_icon} alt="" />
                    <input type="email" placeholder="Email Id"name="" id="" />
                </div>
                <div className="input">
                    <img src={password_icon} alt="" />
                    <input type="password" placeholder="password" name="" id="" />
                </div>
            </div>
            {action==="Sign Up"?<div></div>: <div className="forgot-password">Forgot password? <span>Click Here!</span></div>}
            <div className="submit-container">
                <div className={action==="Login"?"submit gray":"submit"} onClick={()=>{setAction("Sign Up")}}>Sign Up</div>
                <div className={action==="Sigin Up"?"submit gray":"submit"} onClick={()=>{setAction("Login")}}>Login</div>

            </div>
        </div>
    )
}
export default Login
